    public static bool basicallyTheSameColor(Color col1, Color col2)
    {
        if (Mathf.Abs(col1.r - col2.r) < 0.001f && Mathf.Abs(col1.g - col2.g) < 0.001f && Mathf.Abs(col1.b - col2.b) < 0.001f)
        {
            return true;
        }
        return false;
    }

