        }
        catch (Win32Exception ex)
        {
            ex.HelpLink = string.Empty;
        }
    }

    public static void OpenInFileBrowser(string path)
    {
        Game.OpenInWinFileBrowser(path);
        Game.OpenInMacFileBrowser(path);
    }
